mapper.select:
select o.id, o.organization_name, o.code, o.p_id, (select b.organization_name from organization b where b.id = o.p_id) parent_name, o.area_id, o.type, (select a.area_name from area a where a.id = o.area_id) area_name, (select count(*) from organization org where org.p_id = o.id) child, o.create_time FROM organization o where 1=1 and id = #{id} AND o.organization_name LIKE '${organizationName}%' AND o.code = #{code} AND o.p_id = #{pId} AND o.area_id = #{areaId}
ORDER BY ${sortName} ${sortOrder}
ORDER BY o.create_time DESC

SELECT
  id,
  organization_name
from organization
where area_id = (
  SELECT area_id from organization where id = #{organizationId}
)
AND type = #{type}
